import { sample } from './'


export default function calculateRewards(plants, seeds) {
  console.log("Checking rewards")
  if (plants.length % 5 !== 0 || seeds.length === seedBank.length) {
    console.log("No reward")
    return null
  }
  else  {
    return newSeed(seeds)
  }

}

function newSeed(seeds) {
  let duplicate = true
  while (duplicate) {
    let newSeed = sample(seedBank)
    console.log(newSeed)
    duplicate = checkDuplicate(newSeed, seeds)
  }
  console.log("newSeed is")
  console.log(newSeed)
}

function checkDuplicate(newSeed, seeds) {
  for (let index = 0; index < seeds.length; index++) {
    if (newSeed.type === seeds[index].type) {
      return true
    }
    }
  }

const seedBank = [
  {type: "generic_plant", quantity: 3},
  {type: "tulip", quantity: 3},
  {type: "sunflower", quantity: 3},
  {type: "crocus", quantity: 3},
]
