const worldRadiusTop = 1.5
const worldRadiusBottom = 1
const worldScaler = 1.1
const discHeight = 0.3
const rockHeight = 3
const stalactiteMax = {max: 1.6, min: 0.01}
const stalactiteMin = {max: 0.8, min: 0.6}
const stalactiteDepth = {max: 3, min: 1}
const stalactiteSegments = {max: 12, min: 4}

export const world = {
  grass: { geometry: [worldRadiusTop, worldRadiusBottom, discHeight, 25], colour: '#5AAB61' }, // cylinder: [radiusTop, radiusBottom, height, radialSegments]
  earth: { geometry: [worldRadiusTop * worldScaler, discHeight * rockHeight, 0.68, 9], colour: '#8a817c' }, // cylinder: [radiusTop, radiusBottom, height, radialSegments]
  stalactite: { geometry: [terrainRandomiser(stalactiteMax), terrainRandomiser(stalactiteMin), terrainRandomiser(stalactiteDepth), 8], position: [0.1, -0.28, 0], colour: '#6c757d' }
}


function terrainRandomiser(range) {
  return Math.random() * (range.max - range.min) + range.min
}

