import React from 'react'
import './App.css';

import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'

import { NavBar, SignUpButton, Garden } from '../components'
import { SignUp, LogIn } from '../pages'

import 'bootstrap/dist/css/bootstrap.min.css'

class App extends React.Component {

    constructor(props) {
      super(props)
      this.state = {
        userName: "",
        userID: 0,
        garden: []
      }
      this.setUser = this.setUser.bind(this)
    }

    setUser(userName, id, garden) {
      this.setState({
        userName: user,
        id: id,
        garden: garden
      })
      console.log("User is " + this.state.id + " " + this.state.userName)
    }

    render() {
    return (
        <Router>
            <NavBar user={this.state.user} setUser={this.setUser} />
            {this.state.userName ? <Garden size={5} garden={this.state.garden} /> : <LogIn setUser={this.setUser} />}
          </Router>
    )
  }
}

export default App
