import { RoundedBox, Text } from '@react-three/drei'
import React, { useRef, useState } from "react";


// Creates a button that 'clicks'

function SeedBox(props) {

  const mesh = useRef()
  const seeds = props.seeds
  const [clickScale, setClickscale] = useState(1)

  const [seedindex, setSeedindex] = useState(0)

  const handleClick = () => {
    setClickscale(0.97)
    let index = seedindex + 1
    seedindex >= seeds.length - 1 ? setSeedindex(0) : setSeedindex(index)
    setTimeout(() => {
      setClickscale(1)
    }, 100)
  };


return (
  <group>
  <mesh
  {...props}
  ref={mesh}
  onClick={handleClick}
  scale={clickScale}
  >
  <RoundedBox args={[0.2, 0.2, 0.2]} radius={0.05} smoothness={4} {...props}>
  <meshToonMaterial attach="material" color="#212529" />
  </RoundedBox>
  </mesh>
  <SeedBoxText position={[0, 0.4, 0]} seedType={seeds[seedindex].type} quantity={seeds[seedindex].quantity} />
  </group>

)
}

function SeedBoxText(props) {

  const mesh = useRef()


  return
  (
  <mesh
  {...props}
  ref={mesh}
  >
  <Text outlineWidth={0.04} outlineColor="#b1b5c8" color="#293241" rotation={[0, 0, 0]} {...props}>
  seed: {props.seedType} {'\n'}
  quantity: {props.quantity}
  </Text>
  </mesh>
  )
}

export default SeedBox
