import { Billboard, Text } from '@react-three/drei'
import { setDate } from '../functions'


function Noticeboard(props) {
  const date = setDate()

  const noticeTextColour = {
    message: "#293241",
    outline: "#b1b5c8"
  }


return (

  <Billboard
    position={[0.5, 1, 6]}
    args={[20, 200]}
    follow={true}
    lockX={false}
    lockY={false}
    lockZ={false} // Lock the rotation on the z axis (default=false)
    >
    <Text fontSize={1} outlineWidth={0.4} outlineColor={noticeTextColour.outline} color={noticeTextColour.message}> {date.day} {date.month} {date.year}</Text>
    </Billboard>
)
}

export default Noticeboard
