export function TextWithCursor({
  position,
  color = "black",
  fontSize = 0.6,
  width,
  padding,
}: Props) {
  const { buffer, font, cursorIndex } = React.useContext(
    KeyboardInterceptContext
  );
  const textRef = React.useRef<Mesh>();
  const cursorTextRef = React.useRef<Mesh>();
  const [cursorTextMesh, setCursorTextMesh] = React.useState<Mesh>();

  const textShapes = font?.generateShapes(buffer, fontSize);
  const cursorShapes = font?.generateShapes(
    buffer.slice(0, cursorIndex),
    fontSize
  );
  const cursorPosition = getCursorPosition({
    position,
    width,
  });

  React.useEffect(() => {
    setCursorTextMesh(cursorTextRef.current);
  }, [cursorTextRef]);

  return (
    <group position={position.raw()}>
      <Text
        myRef={cursorTextRef}
        position={cursorPosition}
        padding={padding}
        shapes={cursorShapes}
        isTransparent
      />
      <Text
        myRef={textRef}
        position={cursorPosition}
        padding={padding}
        shapes={textShapes}
        color={color}
      />
      <TextCursor
        textMesh={cursorTextMesh}
        position={cursorPosition}
        fontSize={fontSize}
      />
    </group>
  );
}
