import { Billboard, Text } from '@react-three/drei'
import React, { Component, useState, useRef } from 'react';
import {generateBalloonColour} from '../../functions'
const size = 0.3

function BalloonShop(props) {
  const mesh = useRef()

  const handleClick = () => {

  }

  return (
    <mesh
      {...props}
      ref={mesh}
      scale={1}
      onClick={handleClick}
      >
    {balloonsForSale(props)}
    </mesh>
  )
}

function balloonsForSale(props) {
  console.log("In balloonsForSale")
  let balloons = []
  for (ballon = 0; ballon < 6; balloon++) {
    const ballonTextColour = {
      message: "#293241",
      outline: "#b1b5c8"
    }

    balloons.push(<BalloonForSale position={[1, 1, 0]}/>)
  }
  console.log(balloons)
  return balloons
}

function BalloonForSale(props) {

  const mesh = useRef()


  return (
    <mesh
      {...props}
      ref={mesh}
      scale={1}
      >
    <Billboard
      position={props.position}
      follow={true}
      lockX={false}
      lockY={false}
      lockZ={false} // Lock the rotation on the z axis (default=false)
      >
      <Text fontSize={size} outlineWidth={size} outlineColor={ballonTextColour.outline} color={ballonTextColour.message}>
      Hello
       </Text>
      </Billboard>
      </mesh>
    )
}

export default BalloonShop
