function sample(array) {
  return array[Math.floor(Math.random() * array.length)];
}

function checkDuplicate(newSeed, seeds) {
  for (let index = 0; index < seeds.length; index++) {
    const seed = seeds[index]
    if (seed.type === newSeed.type) {
      return true
    }
  }
  return false

}

function updateSeeds(seeds, newSeed, unlockedSeed) {
  const updatedSeeds = []
  for (let index = 0; index < seeds.length; index++) {
    const seed = seeds[index]
    if (newSeed.type = seed.type) {
      seed.quantity += newSeed.quantity
    }
    updatedSeeds.push(seed)
  }
  if (unlockedSeed) { updatedSeeds.push(newSeed) }
  return updatedSeeds
}


