import { Text, Billboard } from '@react-three/drei'
import React, { useRef, useState, useEffect } from "react";
import { createId } from '../../functions'
import { Cloud } from '../'

// Creates a button that 'clicks'

const seedTextColours = {type: "#293241", outline: "#b1b5c8"}
const selectedSeedTextColours = {type: "#b1b5c8", outline: "#293241"}
const fontSize = 0.12
const fontZ = 1
const fontY = 2

function SeedCloud(props) {

  const options = getSeedTypes(props.seeds)
  const [option, setOption] = useState(0)
  const [text, setText] = useState(options[option])
  const mesh = useRef()

  // eventhandler for Cloud

  const handleClick = () => {
    console.log(option)
    option > options.length  ? setOption(0) : setOption(prev => prev + 1)
    console.log(option)

  }

  useEffect(() => {
    setText(options[option])
  }, [option])

  return (
  <>
  <mesh
    {...props}
    ref={mesh}
    scale={1}
    >
    <Cloud options={options} handleClick={handleClick} text={text} />
    {option === 0 ? '' : seedSelectors(props)}
  </mesh>
  </>

)
}

function seedSelectors(props) {
  let seedSelectors = []
  let fontX = 0
  let sortedSeeds = props.seeds.sort()
  for (let index = 0; index < sortedSeeds.length; index++) {
    let seed = sortedSeeds[index]
    if (seed.quantity === 0) { continue }
    const x = 0.5 + Math.sin(Math.PI * index / sortedSeeds.length )
    const y = 2 + Math.cos((Math.PI * index / (sortedSeeds.length * 1.9)  ))
    let colours = {}
    seed.type === props.chosenSeed ? colours = selectedSeedTextColours : colours = seedTextColours
    seedSelectors.push( <SeedText key={createId()} position={[x, y, 0]} selectSeed={props.selectSeed} toggleSeeds={props.toggleSeeds} seed={seed} colours={colours} index={index}  />)
  }
  if (seedSelectors.length === 0) {
    seedSelectors.push( <NoSeedText key={createId()} colours={seedTextColours} index={1} />)
  }
  return seedSelectors
}

function SeedText(props) {
  const seed = props.seed
  const mesh = useRef()
  const [selected, setSelected] = useState(false)

  const handleClick = (event) => {
    event.stopPropagation()
    console.log("Clicked on SeedCloud!")
    console.log(seed.type)
    console.log(props.index)
    props.selectSeed(seed.type, props.index)
  }


  return (
      <mesh
        {...props}
        ref={mesh}
        >
        <Billboard
        follow={true}
        lockX={false}
        lockY={false}
        lockZ={false} >
        < Text
        fontSize={fontSize}
        onClick={(event) => handleClick(event)}
        outlineWidth={fontSize}
        outlineColor={props.colours.outline}
        color={props.colours.type}
        rotation={[0, 0, 0]} >
          {seed.type} {'\n'}
          x {seed.quantity}
        </ Text>
        </ Billboard>
      </mesh>
      )
    }

function NoSeedText(props) {
      const mesh = useRef()

      return (
          <mesh
            {...props}
            ref={mesh}
            >
            <Billboard
            follow={true}
            lockX={false}
            lockY={false}
            lockZ={false} >
            < Text
            fontSize={0.25}
            position={[1, 3, 1]}
            outlineWidth={0.25}
            outlineColor={props.colours.outline}
            color={props.colours.type}
            rotation={[0, 0, 0]} >
              No seeds available
            </ Text>
            </Billboard>
          </mesh>
        )
  }

function getSeedTypes(seeds) {
  let seedTypes = ["seeds"]
  for (let index = 0; index < seeds.length; index++) {
    const seed = seeds[index]
    seedTypes.push(seed.type)
  }
  console.log(seedTypes)
  return seedTypes
}

export default SeedCloud
