import api from '../api';


export default async function makeFriendRequest(id, username) {
  await api.getUser(username).then(res => {
    const userId = id
    const friendId = res.data.data._id
    const friendName = res.data.data.name
    const friendWorld = res.data.data.world
    const payload = {userId, friendId, friendName, friendWorld}

  })
}
