import React, { useRef, useState, useMemo } from "react";
import { useFrame } from "@react-three/fiber";
import * as THREE from "three";


// The plane on which plants grow

function Earth(props) {
  // This reference will give us direct access to the mesh
  const mesh = useRef()
  // Set up state for the hovered and active state
  // Rotate mesh every frame, this is outside of React without overhead
  // useFrame(() => (mesh.current.rotation.y += 0.002))

  const handleClick = (event) => {
    event.stopPropagation()
  }


  return (
    <>
    <mesh
      {...props}
      ref={mesh}
      scale={1}
      onClick={(event) => handleClick(event)}
      >
      <cylinderGeometry args={[1.65, 1, 0.5, 10]}  />
      <meshToonMaterial color={'#947352'} />
      {createStalactites()}
    </mesh>
    </>
  )
}

function createStalactites() {
  return [
    <Stalactite args={[1, 0.8, 1, 5]} position={[0.2, -0.4, 0, ]} colour={'#5c4742'}/>,
    <Stalactite args={[0.7, 0.6, 1, 5]} position={[-0.1, -0.45, 0]} colour={'#5c4742'} />,
    <Stalactite args={[0.6, 0.55, 1, 5]} position={[-0, -2, 0.2]} colour={'#a5978b'} />,
    <Stalactite args={[0.4, 0.3, 1, 5]} position={[0, -1.2, -0.2]} colour={'#6c757d'} />,
    <Stalactite args={[0.3, 0.2, 1, 5]} position={[-0, -1.2, 0]} colour={'#5e503f'} />,
    <Stalactite args={[0.2, 1.5, 1, 5]} position={[-0, -0.6, 0]} colour={'#c4bbaf'}/>,
    <Stalactite args={[-1.2, -1.5, 1, 5]} position={[0, -1, 0]} colour={'#5c4742'} />,



  ]
}

function Stalactite(props) {
  const mesh = useRef()

  return (
    <mesh
      {...props}
      ref={mesh}
      scale={1}
      >
      <cylinderGeometry args={props.args} />
      <meshToonMaterial color={props.colour} />
    </mesh>
  )
}

export default Earth
