import api from '../api';
import { sendBalloon } from './'


export default async function makeFriendRequest(friendUsername, myId, myName) {
  await api.getUser(friendUsername).then(res => {
    console.log(myId)
    console.log(res.data.data.name)
    const friendId = res.data.data._id
    console.log(res.data.data._id)
    const balloons = res.data.data.balloons
    const balloonContents = {
      type: 'friendRequest',
      from: myName,
      message: `${myName} wants to connect`
    }
    console.log(balloons)
    sendBalloon(friendId, balloonContents)
  }).catch(console.log("no user"))
}
